import{_ as l,o as p,c as i,d as o}from"./app.85c8df44.js";var e="/lanjz/assets/sql_1.31a669de.png",s="/lanjz/assets/sql_2.1cbf9aae.png",a="/lanjz/assets/sql_3.83b263f9.png";const r={},n=o('<h1 id="mysql-vs-mongo" tabindex="-1"><a class="header-anchor" href="#mysql-vs-mongo" aria-hidden="true">#</a> mysql VS mongo</h1><ul><li><p>mysql: (SQL)\u5173\u7CFB\u578B\u6570\u636E\u5E93</p></li><li><p>mongo: (NoSQL)\u975E\u5173\u7CFB\u578B\u6570\u636E\u5E93</p></li></ul><h2 id="\u5BF9\u6BD4" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u6BD4" aria-hidden="true">#</a> \u5BF9\u6BD4</h2><p><strong>\u5B58\u50A8\u4E0A</strong></p><ul><li><p>SQL \u901A\u5E38\u4EE5\u6570\u636E\u5E93\u8868\u7684\u5F62\u5F0F\u5B58\u50A8\uFF0C\u4F8B\u5982\u5B58\u50A8\u7528\u6237\u4FE1\u606F\uFF0CSQL\u4E2D\u589E\u52A0\u5916\u90E8\u5173\u7CFB\u7684\u8BDD\uFF0C\u9700\u8981\u5728\u539F\u8868\u4E2D\u589E\u52A0\u4E00\u4E2A\u5916\u952E\uFF0C\u6765\u5173\u8054\u5916\u90E8\u6570\u636E\u8868\u3002\u4F8B\u5982\u9700\u8981\u5728\u501F\u9605\u8868\u4E2D\u589E\u52A0\u5BA1\u6838\u4EBA\u4FE1\u606F\uFF0C\u5148\u5EFA\u7ACB\u4E00\u4E2A\u5BA1\u6838\u4EBA\u8868</p><p><img src="'+e+'" alt=""></p><p>\u518D\u5728\u539F\u6765\u7684\u501F\u9605\u4EBA\u8868\u4E2D\u589E\u52A0\u5BA1\u6838\u4EBA\u5916\u952E</p><p><img src="'+s+'" alt=""></p><p>\u8FD9\u6837\u5982\u679C\u6211\u4EEC\u9700\u8981\u66F4\u65B0\u5BA1\u6838\u4EBA\u4E2A\u4EBA\u4FE1\u606F\u7684\u65F6\u5019\u53EA\u9700\u8981\u66F4\u65B0\u5BA1\u6838\u4EBA\u8868\u800C\u4E0D\u9700\u8981\u5BF9\u501F\u9605\u4EBA\u8868\u505A\u66F4\u65B0</p></li><li><p>NoSql \u91C7\u7528 <code>key-value</code> \u7684\u5F62\u5F0F\u5B58\u50A8</p><p><img src="'+a+'" alt=""></p><p>\u7F3A\u70B9\u4E5F\u6BD4\u8F83\u660E\u663E\uFF0C\u66F4\u65B0\u5BA1\u6838\u4EBA\u6570\u636E\u7684\u65F6\u5019\u5C06\u4F1A\u6BD4\u8F83\u9EBB\u70E6</p></li></ul><p><strong>\u6570\u636E\u8026\u5408\u6027</strong></p><ul><li><p>SQL: \u65E0\u6CD5\u5220\u9664\u88AB\u4F7F\u7528\u7684\u5916\u90E8\u6570\u636E\uFF0C\u4F8B\u5982\u5BA1\u6838\u4EBA\u8868\u4E2D\u7684&quot;\u718A\u4E09&quot;\u5DF2\u7ECF\u88AB\u5206\u914D\u7ED9\u4E86\u501F\u9605\u4EBA\u718A\u5927\uFF0C\u90A3\u4E48\u5728\u5BA1\u6838\u4EBA\u8868\u4E2D\u5C06\u4E0D\u5141\u8BB8\u5220\u9664\u718A\u4E09\u8FD9\u6761\u6570\u636E\uFF0C\u4EE5\u4FDD\u8BC1\u6570\u636E\u5B8C\u6574\u6027\u3002</p></li><li><p>NoSQL: \u6CA1\u6709\u8FD9\u79CD\u5F3A\u8026\u5408\u7684\u6982\u5FF5\uFF0C\u53EF\u4EE5\u968F\u65F6\u5220\u9664\u4EFB\u4F55\u6570\u636E</p></li></ul><p><strong>\u4E8B\u52A1</strong></p><ul><li><p>SQL\u4E2D\u5982\u679C\u591A\u5F20\u8868\u9700\u8981\u540C\u6279\u6B21\u88AB\u66F4\u65B0\uFF0C\u5373\u5982\u679C\u5176\u4E2D\u4E00\u5F20\u8868\u8DDF\u65B0\u5931\u8D25\u7684\u8BDD\uFF0C\u5176\u4ED6\u8868\u4E5F\u4E0D\u4F1A\u66F4\u65B0\u6210\u529F\u3002\u8FD9\u79CD\u573A\u666F\u53EF\u4EE5\u901A\u8FC7\u4E8B\u52A1\u6765\u63A7\u5236\uFF0C\u53EF\u4EE5\u5728\u6240\u6709\u547D\u4EE4\u5B8C\u6210\u4E4B\u540E\uFF0C\u518D\u7EDF\u4E00\u63D0\u4EA4\u4E8B\u52A1</p></li><li><p>\u5728 NoSql \u4E2D\u6CA1\u6709\u4E8B\u52A1\u8FD9\u4E2A\u6982\u5FF5\uFF0C\u6BCF\u4E00\u4E2A\u6570\u636E\u96C6\u90FD\u662F\u539F\u5B50\u7EA7\u522B\u7684</p></li></ul><p><strong>\u6570\u636E\u8868 VS \u6570\u636E\u96C6</strong></p><ul><li><p>\u5173\u7CFB\u578B\u662F\u8868\u683C\u578B\u7684\uFF0C\u5B58\u50A8\u5728\u6570\u636E\u8868\u7684\u884C\u548C\u5217\u4E2D\u3002\u5F7C\u6B64\u5173\u8054\uFF0C\u5BB9\u6613\u63D0\u53D6</p></li><li><p>\u800C\u975E\u5173\u7CFB\u578B\u662F\u5927\u5757\u5B58\u50A8\u5728\u4E00\u8D77</p></li></ul><p><strong>\u9884\u5B9A\u4E49\u7ED3\u6784 VS \u52A8\u6001\u7ED3\u6784</strong></p><ul><li><p>\u5728 SQL \u4E2D\uFF0C\u5FC5\u987B\u5B9A\u4E49\u597D\u5730\u6BB5\u548C\u8868\u7ED3\u6784\u4E4B\u540E\uFF0C\u624D\u80FD\u591F\u6DFB\u52A0\u6570\u636E\uFF0C\u4F8B\u5982\u5B9A\u4E49\u8868\u7684\u4E3B\u952E\u3001\u7D22\u5F15\u3001\u5916\u952E\u7B49\u3002\u8868\u7ED3\u6784\u53EF\u4EE5\u5728\u5B9A\u4E49\u4E4B\u540E\u66F4\u65B0\uFF0C\u4F46\u662F\u5982\u679C\u6709\u6BD4\u8F83\u5927\u7684\u7ED3\u6784\u53D8\u66F4\uFF0C\u5C31\u4F1A\u53D8\u7684\u6BD4\u8F83\u590D\u6742\u3002</p></li><li><p>\u5728 NoSql \u6570\u636E\u5E93\u4E2D\uFF0C\u6570\u636E\u53EF\u4EE5\u5728\u4EFB\u4F55\u65F6\u5019\u4EFB\u4F55\u5730\u65B9\u6DFB\u52A0\u3002\u4E0D\u9700\u8981\u9884\u5148\u5B9A\u4E49</p></li></ul><p><strong>\u5B58\u50A8\u89C4\u8303 VS \u5B58\u50A8\u4EE3\u7801</strong></p><ul><li><p>\u5173\u7CFB\u578B\u6570\u636E\u5E93\u4E3A\u4E86\u89C4\u8303\u6027\uFF0C\u628A\u6570\u636E\u5206\u914D\u6210\u4E3A\u6700\u5C0F\u7684\u903B\u8F91\u8868\u6765\u5B58\u50A8\u907F\u514D\u91CD\u590D\uFF0C\u83B7\u5F97\u7CBE\u7B80\u7684\u7A7A\u95F4\u5229\u7528\u3002\u4F46\u662F\u591A\u4E2A\u8868\u4E4B\u95F4\u7684\u5173\u7CFB\u9650\u5236\uFF0C\u591A\u8868\u7BA1\u7406\u5C31\u6709\u70B9\u590D\u6742\u3002<br> \u5F53\u7136\u7CBE\u7B80\u7684\u5B58\u50A8\u53EF\u4EE5\u8282\u7EA6\u5B9D\u8D35\u7684\u6570\u636E\u5B58\u50A8\uFF0C\u4F46\u662F\u73B0\u5728\u968F\u7740\u793E\u4F1A\u7684\u53D1\u5C55\uFF0C\u78C1\u76D8\u4E0A\u4ED8\u51FA\u7684\u4EE3\u4EF7\u662F\u5FAE\u4E0D\u8DB3\u77E5\u9053\u7684\u3002</p></li><li><p>\u975E\u5173\u7CFB\u578B\u662F\u5E73\u9762\u6570\u636E\u96C6\u5408\u4E2D\uFF0C\u6570\u636E\u7ECF\u5E38\u53EF\u4EE5\u91CD\u590D\uFF0C\u5355\u4E2A\u6570\u636E\u5E93\u5F88\u5C11\u88AB\u5206\u5F00\uFF0C\u800C\u662F\u5B58\u50A8\u6210\u4E3A\u4E00\u4E2A\u6574\u4F53\uFF0C\u8FD9\u79CD\u6574\u5757\u8BFB\u53D6\u6570\u636E\u6548\u7387\u66F4\u9AD8</p></li></ul><p><strong>\u7EB5\u5411\u62D3\u5C55 VS \u6A2A\u5411\u62D3\u5C55</strong></p><ul><li><p>\u4E3A\u4E86\u652F\u6301\u66F4\u591A\u7684\u5E76\u53D1\u91CF\uFF0CSQL\u6570\u636E\u91C7\u7528\u7EB5\u5411\u6269\u5C55\uFF0C\u63D0\u9AD8\u5904\u7406\u80FD\u529B\uFF0C\u901A\u8FC7\u63D0\u9AD8\u8BA1\u7B97\u673A\u6027\u80FD\u6765\u63D0\u9AD8\u5904\u7406\u80FD\u529B</p></li><li><p>NoSql\u901A\u8FC7\u6A2A\u5411\u62D3\u5C55\uFF0C\u975E\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5929\u7136\u662F\u5206\u5E03\u5F0F\u7684\uFF0C\u6240\u4EE5\u53EF\u4EE5\u901A\u8FC7\u96C6\u7FA4\u6765\u5B9E\u73B0\u8D1F\u8F7D\u5747\u8861</p></li></ul><h2 id="\u9009\u62E9" tabindex="-1"><a class="header-anchor" href="#\u9009\u62E9" aria-hidden="true">#</a> \u9009\u62E9</h2><p>\u76EE\u524D\u8BB8\u591A\u5927\u578B\u4E92\u8054\u7F51\u90FD\u4F1A\u9009\u7528MySql+NoSql\u7684\u7EC4\u5408\u65B9\u6848\uFF0C\u56E0\u4E3ASQL\u548CNoSql\u90FD\u6709\u5404\u81EA\u7684\u4F18\u7F3A\u70B9</p><p>\u5173\u7CFB\u578B\u6570\u636E\u5E93\u9002\u5408\u5B58\u50A8\u7ED3\u6784\u5316\u6570\u636E\uFF0C\u6BD4\u5982\uFF1A\u7528\u6237\u7684\u8D26\u53F7\u3001\u5730\u5740\uFF1A</p><ol><li><p>\u8FD9\u4E9B\u6570\u636E\u901A\u5E38\u9700\u8981\u505A\u7ED3\u6784\u5316\u67E5\u8BE2\uFF0C\u6BD4\u5982\u8BF4 <code>Join</code>\uFF0C\u8FD9\u4E2A\u65F6\u5019\uFF0C\u5173\u7CFB\u578B\u6570\u636E\u5E93\u5C31\u8981\u80DC\u51FA\u4E00\u7B79\u3002</p></li><li><p>\u8FD9\u4E9B\u6570\u636E\u7684\u89C4\u6A21\u3001\u589E\u957F\u7684\u901F\u5EA6\u901A\u5E38\u662F\u53EF\u4EE5\u9884\u671F\u7684\u3002</p></li><li><p>\u4E8B\u52A1\u6027\u3001\u4E00\u81F4\u6027,\u9002\u5408\u5B58\u50A8\u6BD4\u8F83\u590D\u6742\u7684\u6570\u636E\u3002</p></li></ol><p>NoSql\u9002\u5408\u5B58\u50A8\u975E\u7ED3\u6784\u5316\u6570\u636E\uFF0C\u6BD4\u5982\uFF1A\u6587\u7AE0\u3001\u8BC4\u8BBA\uFF1A</p><ol><li><p>\u8FD9\u4E9B\u6570\u636E\u901A\u5E38\u7528\u4E8E\u6A21\u7CCA\u5904\u7406\uFF0C\u4F8B\u5982\u5168\u6587\u641C\u7D22\u3001\u673A\u5668\u5B66\u4E60\uFF0C\u9002\u5408\u5B58\u50A8\u8F83\u4E3A\u7B80\u5355\u7684\u6570\u636E\u3002</p></li><li><p>\u8FD9\u4E9B\u6570\u636E\u662F\u6D77\u91CF\u7684\uFF0C\u5E76\u4E14\u589E\u957F\u7684\u901F\u5EA6\u662F\u96BE\u4EE5\u9884\u671F\u7684\u3002</p></li><li><p>\u6309\u7167 <code>key</code> \u83B7\u53D6\u6570\u636E\u6548\u7387\u5F88\u9AD8\uFF0C\u4F46\u662F\u5BF9\u4E8E <code>join</code> \u6216\u5176\u4ED6\u7ED3\u6784\u5316\u67E5\u8BE2\u7684\u652F\u6301\u5C31\u6BD4\u8F83\u5DEE\u3002</p></li></ol><h2 id="\u9644\u52A0" tabindex="-1"><a class="header-anchor" href="#\u9644\u52A0" aria-hidden="true">#</a> \u9644\u52A0</h2><p><strong>\u4E8B\u52A1\u56DB\u5927\u7279\u5F81(ACID)</strong></p><ul><li><p>\u539F\u5B50\u6027(A)\uFF1A\u539F\u5B50\u6027\u662F\u6307\u4E8B\u52A1\u662F\u4E00\u4E2A\u4E0D\u53EF\u518D\u5206\u5272\u7684\u5DE5\u4F5C\u5355\u5143\uFF0C\u4E8B\u52A1\u4E2D\u7684\u64CD\u4F5C\u8981\u4E48\u90FD\u53D1\u751F\uFF0C\u8981\u4E48\u90FD\u4E0D\u53D1\u751F</p></li><li><p>\u4E00\u81F4\u6027(C)\uFF1A\u4E00\u81F4\u6027\u662F\u6307\u5728\u4E8B\u52A1\u5F00\u59CB\u4E4B\u524D\u548C\u4E8B\u52A1\u7ED3\u675F\u4EE5\u540E\uFF0C\u6570\u636E\u5E93\u7684\u5B8C\u6574\u6027\u7EA6\u675F\u6CA1\u6709\u88AB\u7834\u574F\u3002\u8FD9\u662F\u8BF4\u6570\u636E\u5E93\u4E8B\u52A1\u4E0D\u80FD\u7834\u574F\u5173\u7CFB\u6570\u636E\u7684\u5B8C\u6574\u6027\u4EE5\u53CA\u4E1A\u52A1\u903B\u8F91\u4E0A\u7684\u4E00\u81F4\u6027\u3002</p></li><li><p>\u9694\u79BB\u6027(I)\uFF1A\u591A\u4E2A\u4E8B\u52A1\u5E76\u53D1\u8BBF\u95EE\u65F6\uFF0C\u4E8B\u52A1\u4E4B\u95F4\u662F\u9694\u79BB\u7684\uFF0C\u4E00\u4E2A\u4E8B\u52A1\u4E0D\u5E94\u8BE5\u5F71\u54CD\u5176\u5B83\u4E8B\u52A1\u8FD0\u884C\u6548\u679C</p></li><li><p>\u6301\u4E45\u6027(D)\uFF1A\u610F\u5473\u7740\u5728\u4E8B\u52A1\u5B8C\u6210\u4EE5\u540E\uFF0C\u8BE5\u4E8B\u52A1\u6240\u5BF9\u6570\u636E\u5E93\u6240\u4F5C\u7684\u66F4\u6539\u4FBF\u6301\u4E45\u7684\u4FDD\u5B58\u5728\u6570\u636E\u5E93\u4E4B\u4E2D\uFF0C\u5E76\u4E0D\u4F1A\u88AB\u56DE\u6EDA</p></li></ul>',26),t=[n];function d(c,u){return p(),i("div",null,t)}var g=l(r,[["render",d],["__file","index.html.vue"]]);export{g as default};
